import React from 'react'

const ListWidget = ({widget, setWidget, editing}) => {
    return (
        <div>
            <h2>List Widget</h2>
            {
                !editing &&
                    <>
                        {
                            widget.ordered &&
                            <ol>
                                {
                                    widget.text.split("\n").map(item => {
                                        return(
                                            <li>{item}</li>
                                        )
                                    })
                                }
                            </ol>
                        }
                        {
                            !widget.ordered &&
                            <ul>
                                {
                                    widget.text.split("\n").map(item => {
                                        return(
                                            <li>{item}</li>
                                        )
                                    })
                                }
                            </ul>
                        }
                    </>
            }
            {
                editing &&
                <div>
                    <input type="checkbox"/> Ordered
                    <br/>
                    List Items
                    <textarea className="form-control">

                    </textarea>
                </div>
            }
            {/*<textarea></textarea>*/}
        </div>
    )
}

export default ListWidget
