import React from 'react'

const ListWidget = ({widget, setWidget, editing}) => {
    return (
        <div>
            <h2>List Widget</h2>
            {
                !editing &&
                <ol>
                    {
                        widget.text.split("\n").map(item => {
                            return (
                                <li>{item}</li>
                            )
                        })
                    }
                </ol>
                //
                //             {
                //                 !widget.ordered &&
