                //                     {
                //                         widget.text.split("\n").map(item => {
                //                             return(
                //                                 <li>{item}</li>
                //                             )
                //                         })
                //                     }
                //                 </ul>
                //             }
                //         </>
                // }
            }
            {
                editing &&
                <div>
                    <input type="checkbox"/> Ordered
                    <br/>
                    List Items
                    <textarea rows={10} value={widget.text} className="form-control">

                    </textarea>
                </div>
            }

        </div>
    )
};

export default ListWidget
